mapper.update:
update tb_contest
contest_name = #{contestName, jdbcType=VARCHAR}, author = #{author, jdbcType=VARCHAR}, contest_apply_time = #{contestApplyTime, jdbcType=TIMESTAMP}, contest_apply_end_time = #{contestApplyEndTime, jdbcType=TIMESTAMP}, contest_address = #{contestAddress, jdbcType=VARCHAR}, contact_user = #{contactUser, jdbcType=VARCHAR}, contact_phone = #{contactPhone, jdbcType=VARCHAR}, contest_start_time = #{contestStartTime, jdbcType=TIMESTAMP}, contest_end_time = #{contestEndTime, jdbcType=TIMESTAMP}, contest_cooperation = #{contestCooperation, jdbcType=VARCHAR}, contest_introduce = #{contestIntroduce, jdbcType=LONGVARCHAR}, contest_reward = #{contestReward, jdbcType=LONGVARCHAR}, contest_apply_rule = #{contestApplyRule, jdbcType=LONGVARCHAR}, contest_process = #{contestProcess, jdbcType=LONGVARCHAR}, contest_info = #{contestInfo, jdbcType=LONGVARCHAR},
where contest_id = #{contestId, jdbcType=INTEGER}

mapper.select:
select *
from tb_contest
where contest_id = #{contestId, jdbcType=INTEGER}

select apply_result
from tb_team_members
where contest_id = #{contestId, jdbcType=INTEGER}
and open_id = #{openId, jdbcType=VARCHAR}

select team_id
from tb_team
where contest_id = #{contestId, jdbcType=INTEGER}
  and open_id = #{openId, jdbcType=VARCHAR}

select team_name
from tb_team
where contest_id = #{contestId, jdbcType=INTEGER}

select *
from tb_team_members
where contest_id = #{contestId, jdbcType=INTEGER}

select *
from tb_team_members_result
where contest_id = #{contestId, jdbcType=INTEGER}
